function Wallet({
    amount,
    max
}) {
    const classNames = []
    if (amount > max) {
        classNames.push("bg-red", "bg-opacity-10")
    } else {
        classNames.push("bg-white", "border")
    }

    return (
        <div className= {classNames.join(" ")} >
            <div className="py-10 w-96 border-2 flex flex-col justify-center">
                <div >
                    <div className="pb-2">Your Wallet:</div>   
                </div>
                <div className="w-6/12 rounded-full ">
                    <div className="bg-blue-600 text-xs font-medium text-blue-100 text-center py-2 leading-none rounded-full" variant={getProgressBarVariant(amount, max)}
                        min={0}
                        max={max}
                        now={amount}></div>
                </div>
            </div>
        </div>

    )
}

function getProgressBarVariant(amount, max) {
    const ratio = amount / max
    if (ratio < 0.5) return "bg-[#FCAC12]"
    if (ratio < 0.75) return "bg-[#FD3C4A]"
    return "bg-[#00A86B]"
  }

  export default Wallet;
