import { useState, useEffect } from "react";
import axios from "axios";

import ExpenseCard from "../Components/ExpenseCard";
import AddExpense from "../Components/AddExpense";
import Wallet from "../Components/Wallet";

const API_URL = "http://localhost:5005";
let expenseTotal = 0;

function ExpenseListPage() {
  const [expense, setExpense] = useState([]);

  const getExpense = () => {
    const storedToken = localStorage.getItem("authToken");

    // Send the token through the request "Authorization" Headers
    axios
      .get(`${API_URL}/api/expense`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => setExpense(response.data))
      .catch((error) => console.log(error));
  };

  // We set this effect will run only once, after the initial render
  // by setting the empty dependency array - []
  useEffect(() => {
    getExpense();
    Wallet(expenseTotal, 1000)
  }, []);

  return (
    <div>

      {expense.map((_expense) => {
        expenseTotal += _expense.expense;
        return (
          <ExpenseCard
            key={_expense._id}
            title={_expense.title}
            description={_expense.description}
            expense={_expense.expense}
            category={_expense.category}
            firstName={_expense.firstName}
          />
        );
      })}
    </div>
  );
}

export default ExpenseListPage;
