import React from 'react';
import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/auth.context';

function Navbar() {
  const { isLoggedIn, user, logOutUser} = useContext(AuthContext);

  return (
      <nav className="shadow-md w-full  top-0 left-0 bg-[#7F3DFF] flex items-center justify-between lg:px-10 px-7">
       <div
            className="font-bold text-3xl curser-pointer flex items-center font-[Poppins]
        text-[#FCAC12] bg-[#7F3DFF]" //Icon & Name
          >
            <span className="text-3x1 text-[#FCAC12] mr-1 pt-2">

              <ion-icon name="cash-outline"></ion-icon>
            </span>
            Ê›alleon
          </div>
          <div
            onClick={() => setOpen(!open)}
            className="text-3x1 absolute right-8 top-6 curser-pointer lg:hidden "
          >
            <ion-icon name={open ? "close" : "menu"}></ion-icon>
          </div>
          {isLoggedIn && (
              <>
                  <Link to="/profile">
                      <button>Your Profile</button>
                  </Link>
                  <button onClick={logOutUser}>Logout</button>
              </>
          )}
          {!isLoggedIn && (
              <>
                  <div>
                      <Link to="/auth/signup">
                          <button>Sign Up</button>
                      </Link>
                      <Link to="/auth/login">
                          <button>Login</button>
                      </Link>{' '}
                  </div>
              </>
          )}
      </nav>
  );
}

export default Navbar;
