import React from 'react';
import { Link } from 'react-router-dom';
import { useContext } from 'react';
import { useState } from "react";
import { AuthContext } from '../context/auth.context';

function Navbar() {
  const [open, setOpen] = useState(false);
  const { isLoggedIn, logOutUser } = useContext(AuthContext);

  return (
    <nav className="nav">

      <div className="font-bold text-3xl curser-pointer flex items-center  font-[Poppins] text-[#FCAC12] bg-[#7F3DFF]">
        <span className="text-3x1 text-[#FCAC12] mr-1 pt-2">
          {" "}
          <ion-icon name="cash-outline"></ion-icon>
        </span>
        Ê›alleon
      </div>
      <div  onClick={() => setOpen(!open)} className="text-3x1 absolute right-8 top-6 curser-pointer lg:hidden">
        <ion-icon name={open ? "close" : "menu"}></ion-icon>
      </div>

        {isLoggedIn && (
          <>

            <Link to="/profile">
              <button>Your Wallet</button>
            </Link>
            <button onClick={logOutUser}>Logout</button>
          </>
        )}
        {!isLoggedIn && (
          <>
            <div>
              <Link to="/auth/signup">
                <button>Sign Up</button>
              </Link>
              <Link to="/auth/login">
                <button>Login</button>
              </Link>{' '}
            </div>
          </>
        )}
    </nav>
  );
}

export default Navbar;
