// We are deconstructing the props object directly in the parentheses of the function
import expenseIcon from "../Images/icons8-shopping-50.png";

function ExpenseCard({ title, expense, description }) {
  return (
    <div className="max-w-md h-60">
    <div className="flex items-center justify-center bg-[#FD3C4A] ">
      <img className="pr-20"src={expenseIcon}  alt="Expense Icon"/>
      <div>
      <h3>{title}</h3>
      <h4>Amount:</h4>
      <p>{expense}</p>
      <h4>Description:</h4>
      <p>{description}</p>
      </div>
    </div>
      <button>Edit Expense</button>
      <button>Delete Expense</button>
    </div>
  );
}

export default ExpenseCard;
